import HomeNavbar from "../../../components/HomeNavbar/HomeNavbar";
import "./AcademicBlog.css";
import BlogItem from "../../../components/BlogItem";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";

import axios from "axios";

import Config from "../../../Config";

const AcademicBlog = () => {
  const [users, setUsers] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  const fetchBlogs = async (page) => {
    try {
      const response = await axios.get(
        `${Config.baseUrl}/api/blog/all?page=${page}&limit=5`
      );

      if (response.data.success) {
        console.log("The data gotten successfully");
      } else {
        console.log("There was a problem accessing data from the backend");
      }
    } catch (error) {
      console.log(`There was a problem accessing server ${error.message}`);
    }
  };

  useEffect(() => {
    fetchBlogs(page);
  }, [page]);
  const handleNextPage = () => {
    setPage((prevPage) => prevPage + 1);
  };
  const handlePrevPage = () => {
    setPage((prevPage) => prevPage - 1);
  };
  const handlePageChange = (pageNumber) => {
    setPage(pageNumber);
  };

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  const renderPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(
        <button
          key={i}
          onClick={() => handlePageChange(i)}
          disabled={i === page}
        >
          {i}
        </button>
      );
    }
    return pageNumbers;
  };

  return (
    <div className="academic-blog ">
      <HomeNavbar />
      <p className="empty"></p>
      <p className="medium-header blog-header">
        Check Out Sample Assignments Below
      </p>
      <div className="container">
        <div className="search_container flex">
          <Link to="#" className="description flex">
            <div className="row">
              <p>All Posts</p>
              <p>
                <i class="fa-solid fa-arrow-right"></i>
              </p>
            </div>
          </Link>
          <div className="search_bar flex">
            <input type="text" placeholder="Type the blog title..." />
            <i class="fa-solid fa-magnifying-glass"></i>
          </div>
        </div>
        <div className="blogs">
          <BlogItem />
        </div>
        {renderPageNumbers()}
        <button
          onClick={handleNextPage}
          disabled={page === totalPages}
          className="navigation-btns"
        >
          Next
        </button>
        <p>
          Page {page} of {totalPages}
        </p>
      </div>
    </div>
  );
};

export default AcademicBlog;
