import React, { useState } from "react";

const AddBlogModal = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [reference, setReference] = useState("");
  const [image, setImage] = useState(null);
  const [titleError, setTitleError] = useState("");
  const [descriptionError, setDescriptionError] = useState("");
  const [referenceError, setReferenceError] = useState("");
  const [imageError, setImageError] = useState("");
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
    }
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    let formIsValid = true;
    if (!title.trim()) {
      setTitleError("Please enter the blog's title.");
      formIsValid = false;
    } else {
      setTitleError("");
    }
    if (!description.trim()) {
      setDescriptionError("Please enter the blog's description.");
      formIsValid = false;
    } else {
      setDescriptionError("");
    }
    if (!reference.trim()) {
      setReferenceError("Please enter the blog's reference.");
      formIsValid = false;
    } else {
      setReferenceError("");
    }
    if (!image) {
      setImageError("Please select an image.");
      formIsValid = false;
    } else {
      setImageError("");
    }
    if (formIsValid) {
      console.log("Form submitted:", { title, description, reference, image });
      setTitle("");
      setDescription("");
      setReference("");
      setImage(null);
    }
  };
  return (
    <form onSubmit={handleSubmit} action="#">
      <p className="medium-header">ADD BLOG</p>
      <div className="input-group">
        <input
          type="text"
          name="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Enter the blog's title..."
        />
        {titleError && <p className="error">{titleError}</p>}
      </div>
      <textarea
        name="description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        cols="30"
        rows="10"
        placeholder="Enter blog's description..."
      ></textarea>
      {descriptionError && <p className="error">{descriptionError}</p>}
      <div className="input-group">
        <input
          type="text"
          name="reference"
          value={reference}
          onChange={(e) => setReference(e.target.value)}
          placeholder="Enter the blog's reference..."
        />
        {referenceError && <p className="error">{referenceError}</p>}
      </div>
      <input
        type="file"
        name="image"
        id="image"
        onChange={handleImageChange}
        accept="image/*"
        style={{marginTop:"1rem"}}
      />
      {imageError && <p className="error" style={{color:"red"}}>{imageError}</p>}
      {image && (
        <div className="image-preview">
          <img src={URL.createObjectURL(image)} alt="Selected" />
        </div>
      )}
      <input
        type="submit"
        value="ADD BLOG"
        className="submit-btn"
        style={{ marginTop: "1rem" }}
      />
    </form>
  );
};
export default AddBlogModal;
