const AddBlogModal = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [reference, setReference] = useState("");
  const [image, setImage] = useState(null);
  const [titleError, setTitleError] = useState("");
  const [descriptionError, setDescriptionError] = useState("");
  const [referenceError, setReferenceError] = useState("");
  const [imageError, setImageError] = useState("");

  const sendDataToBackend = async () => {
    try{
      const response = await axios.post(`http://localhost:5000/api/blog/post/`)
    }catch(error){
      console.log(`There was an error sending data tot the backend`)
    }

  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
    }
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
    if (titleError) {
      setTitleError("");
    }
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
    if (descriptionError) {
      setDescriptionError("");
    }
  };

  const handleReferenceChange = (e) => {
    setReference(e.target.value);
    if (referenceError) {
      setReferenceError("");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    let formIsValid = true;

    if (!title.trim()) {
      setTitleError("Please enter the blog's title.");
      formIsValid = false;
    }

    if (!description.trim()) {
      setDescriptionError("Please enter the blog's description.");
      formIsValid = false;
    }

    if (!reference.trim()) {
      setReferenceError("Please enter the blog's reference.");
      formIsValid = false;
    }

    if (!image) {
      setImageError("Please select an image.");
      formIsValid = false;
    }

    if (formIsValid) {
      console.log("Form submitted:", { title, description, reference, image });
      setTitle("");
      setDescription("");
      setReference("");
      setImage(null);
    }
  };

  return (
    <form onSubmit={handleSubmit} action="#">
      <p className="medium-header">ADD BLOG</p>

      <div className="input-group">
        <input
          type="text"
          name="title"
          value={title}
          onChange={handleTitleChange}
          placeholder="Enter the blog's title..."
        />
        {titleError && <p className="error">{titleError}</p>}
      </div>

      <div className="input_group">
        <textarea
          name="description"
          value={description}
          onChange={handleDescriptionChange}
          cols="30"
          rows="20"
          placeholder="Enter blog's description..."
        ></textarea>
        {descriptionError && <p className="error">{descriptionError}</p>}
      </div>

      <div className="input-group">
        <input
          type="text"
          name="reference"
          value={reference}
          onChange={handleReferenceChange}
          placeholder="Enter the blog's reference..."
        />
        {referenceError && <p className="error">{referenceError}</p>}
      </div>

      <div className="input_group">
        <input
          type="file"
          name="image"
          id="image"
          onChange={handleImageChange}
          accept="image/*"
          style={{ marginTop: "1rem" }}
        />
        {imageError && (
          <p className="error" style={{ color: "red" }}>
            {imageError}
          </p>
        )}
      </div>

      {image && (
        <div className="image-preview">
          <img src={URL.createObjectURL(image)} alt="Selected" />
        </div>
      )}

      <input
        type="submit"
        value="ADD BLOG"
        className="submit-btn"
        style={{ marginTop: "1.5rem" }}
      />
    </form>
  );
};

export default AddBlogModal;
