import ShareblogModal from "./ShareblogModal";
import { useState } from "react";
import BlogProfileCard from "./BlogProfileCard";
const BlogItem = () => {
  const [shareBlog, showShareBlogModal] = useState(false);
  const [notification, showNotification] = useState(false);
  const handleShowNotification = () => {
    showNotification(!notification);
    console.log(notification);
  };
  const handleShowBlogModal = () => {
    showShareBlogModal(!shareBlog);
  };
  return (
    <div className="card flex">
      <div className="col">
        <img
          src="https://plus.unsplash.com/premium_photo-1681755915233-9acafb348a7f?q=80&w=1470&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
          alt=""
        />
      </div>
      <div className="col">
        <div className="profile flex">
          <BlogProfileCard />
          <p className="eclipseIcon" onClick={handleShowNotification}>
            <i class="fas fa-ellipsis-v"></i>
          </p>
          <div
            className={`share-blog ${notification ? "flex" : null}`}
            onClick={handleShowBlogModal}
          >
            <p>
              <i class="fa-solid fa-share"></i>
            </p>
            <p className="medium-header">Share</p>
          </div>
        </div>
        <p className="large-headers">
          The Yellow Wallpaper Literary Analysis (MLA 8 Referencing Style)
        </p>
        <p className="description">
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Dolor
          quaerat aperiam et blanditiis nihil sunt perferendis, autem, corrupti
          quae, obcaecati voluptas assumenda consectetur? Vel cumque magni velit
          ducimus consequuntur eos!
        </p>
        <div className="control-icons flex">
          <p>14 views</p>
          <p>0 comments</p>
          <p>
            1 <i class="fa-regular fa-heart"></i>
          </p>
        </div>
      </div>
      <ShareblogModal
        handleShowBlogModal={handleShowBlogModal}
        shareBlog={shareBlog}
      />
    </div>
  );
};

export default BlogItem;
