import HomeNavbar from "../../../components/HomeNavbar/HomeNavbar";
import "../Academicblogs/AcademicBlog.css";
import "./SingleBlog.css";
import SingleBlogComponent from "../../../components/SingleBlogComponent";
import Footer from "../../../components/Footer/Footer";
import RecentPost from "../../../components/RecentPost";
import { useEffect } from "react";
const SingleBlogPage = () => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  return (
    <div className="academic-blog single-blogs">
      <HomeNavbar />
      <p className="empty"></p>
      <div className="container">
        <SingleBlogComponent />
      </div>
      <div className="container">
        <p className="medium-header" style={{ textAlign: "start" }}>
          Recent Posts
        </p>
      </div>
      <div className="container">
        <div className="grid">
          <RecentPost />
          <RecentPost />
          <RecentPost />
        </div>
      </div>
      <div className="container">
        <div className="comment-container">
          <p className="large-headers">Comments</p>
          <textarea
            name=""
            id=""
            cols="30"
            rows="10"
            placeholder="Write a comment..."
          ></textarea>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default SingleBlogPage;
