import React, { useState, useEffect } from "react";
import axios from "axios";
import Config from "../../Config";

const EditBlogModal = ({ blog,fetchBlogs,page,handleEditModal }) => {
  const [title, setTitle] = useState(blog?.title || "");
  const [description, setDescription] = useState(blog?.description || "");
  const [reference, setReference] = useState(blog?.reference || "");
  const [image, setImage] = useState(null);
  const [titleError, setTitleError] = useState("");
  const [descriptionError, setDescriptionError] = useState("");
  const [referenceError, setReferenceError] = useState("");
  const [imageError, setImageError] = useState("");
  const [uploadSuccess, setUploadSuccess] = useState(false);

  useEffect(() => {
    setTitle(blog?.title || "");
    setDescription(blog?.description || "");
    setReference(blog?.reference || "");
  }, [blog]);

  const sendDataToBackend = async () => {
    try {
      const formData = new FormData();
      if (title) formData.append("title", title);
      if (description) formData.append("description", description);
      if (reference) formData.append("reference", reference);
      if (image) formData.append("file", image);

      const response = await axios.patch(
        `${Config.baseUrl}/api/blog/update/${blog._id}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      console.log("Response from backend:", response.data);
      setUploadSuccess(true);
    } catch (error) {
      console.error("Error sending data to backend:", error);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
    }
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
    if (titleError) {
      setTitleError("");
    }
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
    if (descriptionError) {
      setDescriptionError("");
    }
  };

  const handleReferenceChange = (e) => {
    setReference(e.target.value);
    if (referenceError) {
      setReferenceError("");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let formIsValid = true;

    if (!title.trim()) {
      setTitleError("Please enter the blog's title.");
      formIsValid = false;
    }

    if (!description.trim()) {
      setDescriptionError("Please enter the blog's description.");
      formIsValid = false;
    }

    if (!reference.trim()) {
      setReferenceError("Please enter the blog's reference.");
      formIsValid = false;
    }

    if (!image) {
      setImageError("Please select an image.");
      formIsValid = false;
    }

    if (formIsValid) {
      await sendDataToBackend();
      await fetchBlogs(page)
      handleEditModal()
    }
  };

  return (
    <form onSubmit={handleSubmit} action="#">
      <p className="medium-header">EDIT BLOG</p>
      <p style={{ textAlign: "center" }}>
        Note!! At the end of every paragraph in the description input `\n` for
        system to know the beginning of a new paragraph
      </p>
      <div className="input-group">
        <input
          type="text"
          name="title"
          value={title}
          onChange={handleTitleChange}
          placeholder="Enter the blog's title..."
        />
        {titleError && <p className="error">{titleError}</p>}
      </div>

      <div className="input_group">
        <textarea
          name="description"
          value={description}
          onChange={handleDescriptionChange}
          cols="30"
          rows="20"
          placeholder="Enter blog's description..."
        ></textarea>
        {descriptionError && <p className="error">{descriptionError}</p>}
      </div>

      <div className="input-group">
        <input
          type="text"
          name="reference"
          value={reference}
          onChange={handleReferenceChange}
          placeholder="Enter the blog's reference..."
        />
        {referenceError && <p className="error">{referenceError}</p>}
      </div>

      <div className="input_group">
        <input
          type="file"
          name="image"
          id="image"
          onChange={handleImageChange}
          accept="image/*"
          style={{ marginTop: "1rem" }}
        />
        {imageError && (
          <p className="error" style={{ color: "red" }}>
            {imageError}
          </p>
        )}
      </div>

      {image && (
        <div className="image-preview">
          <img src={URL.createObjectURL(image)} alt="Selected" />
        </div>
      )}

      {uploadSuccess && (
        <p style={{ color: "green", marginTop: "1rem" }}>
          Blog updated successfully!
        </p>
      )}

      <input
        type="submit"
        value="UPDATE BLOG"
        className="submit-btn"
        style={{ marginTop: "1.5rem" }}
      />
    </form>
  );
};

export default EditBlogModal;
