import React, { useState } from "react";
import "./login.css";
import "./authentication.css";
import Welcome from "./Welcome";
import { Link, useNavigate } from "react-router-dom";
import Config from "../../../Config";
import { useSelector, useDispatch } from "react-redux";
import { showLoading, hideLoading } from "../../../Redux/features/AlertSlice";
import Preloader from "../../../Preloader/Preloader";
import axios from "axios";
const Login = () => {
  const loading = useSelector((state) => state.alerts.loading);
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [errors, setErrors] = useState({});
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
    // Clear the error for the field being changed
    setErrors({
      ...errors,
      [name]: "",
    });
  };
  const validate = () => {
    let errors = {};
    if (!formData.email) {
      errors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = "Email address is invalid";
    }
    if (!formData.password) {
      errors.password = "Password is required";
    }
    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const validationErrors = validate();

    if (Object.keys(validationErrors).length === 0) {
      const response = await axios.post(
        `${Config.baseUrl}/api/auth/login`,
        {
          email: formData.email,
          password: formData.password,
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      console.log("Form submitted successfully:");

      if (response.data.success) {
        console.log("The form was successfully submitted");
      } else {
        if (response.data.email) {
          setErrors({ ...errors, email: response.data.email });
        }
        if (response.data.password) {
          setErrors({ ...errors, password: response.data.password });
        }
      }

      // navigate("/client/complete-orders");
    } else {
      setErrors(validationErrors);
    }
  };

  return (
    <div className="authentication">
      <div className="authentication_form_container">
        <div className="container">
          <div className="medium-header">LOGIN</div>
          <form onSubmit={handleSubmit} className="authentication_form">
            <div className="input-group">
              <i className="fa-solid fa-envelope"></i>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your email"
              />
              {errors.email && <span className="error">{errors.email}</span>}
            </div>
            <div className="input-group">
              <i className="fa-solid fa-lock"></i>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter your password"
              />
              {errors.password && (
                <span className="error">{errors.password}</span>
              )}
            </div>
            <input
              type="submit"
              value="LOGIN"
              className="submit-btn"
              style={{ marginTop: "0.6rem" }}
            />
            <Link to="/forgot-password">Forgot password?</Link>
          </form>
        </div>
      </div>
      <Welcome />
      {loading && <Preloader />}
    </div>
  );
};

export default Login;
