import React, { useState } from 'react';
import './login.css';
import './authentication.css';
import Welcome from './Welcome';
import { Link } from 'react-router-dom';

const ChangePassword = () => {
  const [formData, setFormData] = useState({
    email: '',
    oldPassword: '',
    newPassword: '',
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    // Clear the error for the field being changed
    setErrors({
      ...errors,
      [name]: '',
    });
  };

  const validate = () => {
    let errors = {};
    if (!formData.email) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Email address is invalid';
    }
    if (!formData.oldPassword) {
      errors.oldPassword = 'Old password is required';
    }
    if (!formData.newPassword) {
      errors.newPassword = 'New password is required';
    }
    return errors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validate();
    if (Object.keys(validationErrors).length === 0) {
      console.log('Form submitted successfully:', formData);
      // Perform form submission logic here
    } else {
      setErrors(validationErrors);
    }
  };

  return (
    <div className="authentication">
      <div className="authentication_form_container">
        <div className="container">
          <div className="medium-header">CHANGE PASSWORD</div>
          <form onSubmit={handleSubmit} className="authentication_form">
            <div className="input-group">
              <i className="fa-solid fa-envelope"></i>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your email"
              />
              {errors.email && <span className="error">{errors.email}</span>}
            </div>
            <div className="input-group">
              <i className="fa-solid fa-lock"></i>
              <input
                type="password"
                name="oldPassword"
                value={formData.oldPassword}
                onChange={handleChange}
                placeholder="Enter your old password"
              />
              {errors.oldPassword && <span className="error">{errors.oldPassword}</span>}
            </div>
            <div className="input-group">
              <i className="fa-solid fa-lock"></i>
              <input
                type="password"
                name="newPassword"
                value={formData.newPassword}
                onChange={handleChange}
                placeholder="Enter your new password"
              />
              {errors.newPassword && <span className="error">{errors.newPassword}</span>}
            </div>
            <input
              type="submit"
              value="CHANGE PASSWORD"
              className="submit-btn"
              style={{marginTop:'0.6rem'}}
            />
            <Link to="/login">Have an account? Login</Link>
          </form>
        </div>
      </div>
      <Welcome />
    </div>
  );
};

export default ChangePassword;
