import React, { useState } from "react";
import axios from "axios";
import Config from "../../Config";

const EditBlogModal = ({ blog }) => {
  const [title, setTitle] = useState(blog.title);
  const [description, setDescription] = useState(blog.description);
  const [reference, setReference] = useState(blog.reference);
  const [image, setImage] = useState(null);
  const [titleError, setTitleError] = useState("");
  const [descriptionError, setDescriptionError] = useState("");
  const [referenceError, setReferenceError] = useState("");
  const [imageError, setImageError] = useState("");
  const [uploadSuccess, setUploadSuccess] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
    }
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
    if (titleError) {
      setTitleError("");
    }
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
    if (descriptionError) {
      setDescriptionError("");
    }
  };

  const handleReferenceChange = (e) => {
    setReference(e.target.value);
    if (referenceError) {
      setReferenceError("");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let formIsValid = true;

    if (!title.trim()) {
      setTitleError("Please enter the blog's title.");
      formIsValid = false;
    }

    if (!description.trim()) {
      setDescriptionError("Please enter the blog's description.");
      formIsValid = false;
    }

    if (!reference.trim()) {
      setReferenceError("Please enter the blog's reference.");
      formIsValid = false;
    }

    // Check if image is present only if it's selected
    if (image && !image.name.match(/\.(jpg|jpeg|png|gif)$/)) {
      setImageError("Please select a valid image file (jpg/jpeg/png/gif).");
      formIsValid = false;
    }

    if (formIsValid) {
      try {
        const formData = new FormData();
        formData.append("title", title);
        formData.append("description", description);
        formData.append("reference", reference);

        // Append image only if it's selected
        if (image) {
          formData.append("file", image);
        }

        const response = await axios.patch(
          `${Config.baseUrl}/api/blog/update/${blog._id}`,
          formData,
          {
            headers: {
              "Content-Type": "multipart/form-data",
            },
          }
        );

        console.log("Response from backend:", response.data);
        setTitle("");
        setDescription("");
        setReference("");
        setImage(null);
        setTitleError("");
        setDescriptionError("");
        setReferenceError("");
        setImageError("");
        setUploadSuccess(true);
      } catch (error) {
        console.error("Error sending data to backend:", error);
      }
    }
  };

  return (
    <form onSubmit={handleSubmit} action="#">
      <p className="medium-header">EDIT BLOG</p>
      <div className="input-group">
        <input
          type="text"
          name="title"
          value={title}
          onChange={handleTitleChange}
          placeholder="Enter the blog's title..."
        />
        {titleError && <p className="error">{titleError}</p>}
      </div>

      <div className="input_group">
        <textarea
          name="description"
          value={description}
          onChange={handleDescriptionChange}
          cols="30"
          rows="10"
          placeholder="Enter blog's description..."
        ></textarea>
        {descriptionError && <p className="error">{descriptionError}</p>}
      </div>

      <div className="input-group">
        <input
          type="text"
          name="reference"
          value={reference}
          onChange={handleReferenceChange}
          placeholder="Enter the blog's reference..."
        />
        {referenceError && <p className="error">{referenceError}</p>}
      </div>

      <div className="input_group">
        <input
          type="file"
          name="image"
          id="image"
          onChange={handleImageChange}
          accept="image/*"
          style={{ marginTop: "1rem" }}
        />
        {imageError && (
          <p className="error" style={{ color: "red" }}>
            {imageError}
          </p>
        )}
      </div>

      {blog.imageUrl && !image && ( // Display current image only if no new image selected
        <div className="image-preview">
          <img src={`${Config.baseUrl}/uploads/blogs/${blog.imageUrl}`} alt="Current Blog" />
        </div>
      )}

      {uploadSuccess && (
        <p style={{ color: "green", marginTop: "1rem" }}>
          Blog updated successfully!
        </p>
      )}

      <input
        type="submit"
        value="UPDATE BLOG"
        className="submit-btn"
        style={{ marginTop: "1.5rem" }}
      />
    </form>
  );
};

export default EditBlogModal;
