    }catch(error){
      console.log(`There was an error fetching the data from the backend ${error.message}`)
    }
  }

  useEffect(() => {
    fetchSingleBlog();
  }, [blogId]);

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className="academic-blog single-blogs">
      <HomeNavbar />
      {loading ? (
        <p>Loading...</p>
      ) : blog ? (
        <>
          <div className="container" style={{marginTop:"60px"}}>
            <SingleBlogComponent blog={blog} />
          </div>
          <div className="container">
            <p className="medium-header" style={{ textAlign: "start" }}>
              Recent Posts
            </p>
          </div>
          <div className="container">
            <div className="grid">
              <RecentPost />
              <RecentPost />
              <RecentPost />
            </div>
          </div>
          <div className="container">
            <div className="comment-container">
              <p className="large-headers">Comments</p>
              <textarea
                name=""
                id=""
                cols="30"
                rows="10"
                placeholder="Write a comment..."
              ></textarea>
            </div>
          </div>
        </>
      ) : (
        <p>No blog found for ID: {blogId}</p>
      )}
      <Footer />
    </div>
  );
};

export default SingleBlogPage;
