import AdminNavbar from "../../components/Admin/AdminNavbar";

import AdminSidebar from "../../components/Admin/AdminSidebar";

import Config from "../../Config";

import { useState, useEffect } from "react";

import "./administrationBlog.css";

import "./Admin.css";

import axios from "axios";

import AdminBlogsList from "../../components/Admin/AdminBlogsList";

const AdministrationBlog = () => {
  const [blogs, setBlogs] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    fetchBlogs(page);
  }, [page]);
  const fetchBlogs = async (pageNumber) => {
    try {
      const response = await axios.get(
        `${Config.baseUrl}/api/blog/all?page=${pageNumber}&limit=5`
      );
      if (response.data.success) {
        setBlogs(response.data.data);
        setTotalPages(response.data.totalPages);
      } else {
        console.log("There was a problem accessing data from the backend");
      }
    } catch (error) {
      console.log(`There was a problem accessing server ${error.message}`);
    }
  };

  const handleNextPage = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const handlePrevPage = () => {
    setPage((prevPage) => prevPage - 1);
  };

  const handlePageChange = (pageNumber) => {
    setPage(pageNumber);
  };

  const renderPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      const buttonClass =
        i === page ? "navigation-btns current-page" : "navigation-btns";
      pageNumbers.push(
        <button
          key={i}
          onClick={() => handlePageChange(i)}
          className={buttonClass}
        >
          {i}
        </button>
      );
    }
    return pageNumbers;
  };

  const [sidebar, showSidebar] = useState(false);

  const handleShowSidebar = () => {
    showSidebar(!sidebar);
  };

  return (
    <div className="profile admin blogs">
      <AdminNavbar
        sidebar={sidebar}
        handleShowSidebar={handleShowSidebar}
        title="BLOGS"
      />
      <AdminSidebar sidebar={sidebar} />
      <p className="empty"></p>
      <div className="container">
        <div className="grid">
          <div className="col">
            <p className="medium-header">ADD BLOGS</p>
            <form action="#">
              <div className="input-group">
                <input
                  type="text"
                  name=""
                  id=""
                  placeholder="Enter the blogs title..."
                />
              </div>
              <textarea name="" id="" cols="30" rows="20" placeholder="Enter blog's description..."></textarea>
              <input type="submit" value="ADD BLOG" className="submit-btn" />
            </form>
          </div>
          <div className="col">
            <p className="medium-header">EDIT OR DELETE BLOGS</p>
            <div className="table_wrapper" id="blogs-table">
              <table>
                <thead>
                  <td>IMAGE</td>
                  <td>VIEWS</td>
                  <td>COMMENTS</td>
                  <td>LIKES</td>
                  <td>Edit</td>
                  <td>Delete</td>
                </thead>
                <tbody>
                  {blogs.map((blog) => (
                    <AdminBlogsList blog={blog} />
                  ))}
                </tbody>
              </table>
            </div>
            <div className="pagination" style={{ marginTop: "10px" }}>
              {renderPageNumbers()}
              <button
                onClick={handlePrevPage}
                disabled={page === 1}
                className="navigation-btns"
              >
                Prev
              </button>
              <button
                onClick={handleNextPage}
                disabled={page === totalPages}
                className="navigation-btns"
              >
                Next
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdministrationBlog;
