import AdminNavbar from "../../components/Admin/AdminNavbar";
import AdminSidebar from "../../components/Admin/AdminSidebar";
import Config from "../../Config";
import { useState, useEffect } from "react";
import "./administrationBlog.css";
import axios from "axios";

const AdministrationBlog = () => {
  const [blogs, setBlogs] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    fetchBlogs(page);
  }, [page]); // Fetch blogs whenever page changes

  const fetchBlogs = async (pageNumber) => {
    try {
      const response = await axios.get(
        `${Config.baseUrl}/api/blog/all?page=${pageNumber}&limit=5`
      );
      if (response.data.success) {
        setBlogs(response.data.data);
        setTotalPages(response.data.totalPages);
      } else {
        console.log("There was a problem accessing data from the backend");
      }
    } catch (error) {
      console.log(`There was a problem accessing server ${error.message}`);
    }
  };

  const handleNextPage = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const handlePrevPage = () => {
    setPage((prevPage) => prevPage - 1);
  };

  const handlePageChange = (pageNumber) => {
    setPage(pageNumber);
  };

  const renderPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(
        <button
          key={i}
          onClick={() => handlePageChange(i)}
          className={i === page ? "active" : ""}
        >
          {i}
        </button>
      );
    }
    return pageNumbers;
  };

  const [sidebar, showSidebar] = useState(false);

  const handleShowSidebar = () => {
    showSidebar(!sidebar);
  };

  return (
    <div className="profile admin">
      <AdminNavbar
        sidebar={sidebar}
        handleShowSidebar={handleShowSidebar}
        title="PROFILE"
      />
      <AdminSidebar sidebar={sidebar} />
      <div className="container">
        <div className="grid">
          <div className="col">
            <p className="medium-header">ADMINISTRATION BLOGS</p>
          </div>
          <div className="col">
            <p className="medium-header">ADD BLOGS</p>
          </div>
          <div className="col">
            <p className="medium-header">EDIT OR DELETE BLOGS</p>
            <div className="table_wrapper">
              <table>
                <thead>
                  <tr>
                    <th>IMAGE</th>
                    <th>TITLE</th>
                    <th>VIEWS</th>
                    <th>COMMENTS</th>
                    <th>LIKES</th>
                    <th>Edit</th>
                    <th>Delete</th>
                  </tr>
                </thead>
                <tbody>
                  {blogs.map((blog) => (
                    <tr key={blog._id}>
                      <td>
                        <img
                          src={`${Config.baseUrl}/uploads/blogs/${blog.imageUrl}`}
                          alt={blog.title}
                          className="blog-image"
                        />
                      </td>
                      <td>{blog.title}</td>
                      <td>{blog.views}</td>
                      <td>{blog.comments}</td>
                      <td>{blog.likes}</td>
                      <td>
                        <button>Edit</button>
                      </td>
                      <td>
                        <button>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div className="pagination">
              {renderPageNumbers()}
              <button
                onClick={handlePrevPage}
                disabled={page === 1}
                className="navigation-btns"
              >
                Prev
              </button>
              <button
                onClick={handleNextPage}
                disabled={page === totalPages}
                className="navigation-btns"
              >
                Next
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdministrationBlog;
